package forgefuck.team.xenobyte.render;

import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.File;
import java.io.IOException;
import java.util.Base64;

import javax.imageio.ImageIO;

public class Images {
    
    public static final BufferedImage FONT = decodeImage("");
    public static final BufferedImage ICON = decodeImage("iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAIAAACQkWg2AAAALElEQVR42mNgIAP8JxpQpgGrtficNDg1/F8ARSgaTvyHIipoGA6hRDMNJAEAqVn3gSGwLEcAAAAASUVORK5CYII=");
    
    public static BufferedImage decodeImage(String in) {
        try {
            return ImageIO.read(new ByteArrayInputStream(Base64.getDecoder().decode(in)));
        } catch (IOException e) {
            return null;
        }
    }
    
    public static String encode(BufferedImage in) {
        ByteArrayOutputStream out = new ByteArrayOutputStream();
        try {
            ImageIO.write(in, "png", out);
            String signature = Base64.getEncoder().encodeToString(out.toByteArray());
            out.close();
            return signature;
        } catch(Exception e) {
            return null;
        }
    }
    
    public static String encode(File file) {
        try {
            return encode(ImageIO.read(file));
        } catch (IOException e) {
            return null;
        }
    }

}
